// Get all elements with class="star"
var stars = document.getElementsByClassName("star");
for (var i = 0; i < stars.length; i++) {
    let star = stars[i];
    let starLabel = document.createElement("div");
    starLabel.innerHTML = star.getAttribute("data-label");
    starLabel.className = "cosmic-label";
    star.appendChild(starLabel);
    star.style.transform = star.getAttribute("data-transform");
    let starIcon = document.createElement("img");
    starIcon.className = "cosmic-icon";
    starIcon.src = star.getAttribute("data-icon");
    star.appendChild(starIcon);
}

// Add random stars to the elements with class="star-layer"
var scenes = document.getElementsByClassName("star-layer");
for (var scene of scenes) {
    for (var i = 0; i < 32; i++) {
        let star = document.createElement("div");
        star.className = "small-star";
        star.setAttribute("data-depth", Math.random() * 2);
        star.style.left = (Math.random() * 100)*2 + "vw";
        star.style.top = (Math.random() * 100)*2 + "vh";
        star.style.transform = "translateX(-" + Math.random() * 100 + "vw) translateY(-" + Math.random() * 100 + "vh)";
        // random size
        let size = 4 + Math.abs(Math.random() * 4);
        star.style.width = size + "px";
        star.style.height = size + "px";
        // random brightness
        let brightness = Math.random() * 100;
        star.style.filter = "brightness(" + brightness + "%)";
        // random color
        let color = Math.random() * 360;
        star.style.backgroundColor = "hsl(" + color + ", 100%, 50%)";
        scene.appendChild(star);
    }
    scene.setAttribute("data-depth", Math.random() * 2);
}

// Initalize parallax
var scene = document.getElementById('scene');
var parallaxInstance = new Parallax(scene);